mapper.update:

UPDATE fm_board
SET title=#{title}, category=#{category}, CONTENT=#{content}, update_ts=current_date
WHERE bno=#{bno}


UPDATE fm_board
SET delete_ts=current_date
WHERE bno=#{bno}

UPDATE fm_board 
SET view_cnt = view_cnt+#{amount}
where bno=#{bno}

UPDATE fm_board 
SET comment_cnt = comment_cnt + #{amount}
WHERE bno=#{bno}

UPDATE fm_board 
SET like_cnt = like_cnt + #{amount}
WHERE bno=#{bno}
