mapper.update:

UPDATE fm_review
SET content=#{content}, rate=#{rate}, update_ts=current_date
WHERE rno=#{rno}


			{
		    	CALL
		        DECLARE
		        BEGIN
		            UPDATE fm_review SET delete_ts = current_date WHERE rno = #{rno} AND film_id = #{filmId};
		            UPDATE fm_review_comment SET delete_ts = current_date WHERE rno = #{rno};
		            DELETE FROM fm_review_liked WHERE rno = #{rno};
		        END
    		}


UPDATE fm_review_comment
SET content=#{content}, update_ts=current_date
WHERE rcno=#{rcno}


        	{
		    	CALL
		        DECLARE
		        BEGIN
		            UPDATE fm_review_comment SET delete_ts = current_date WHERE rcno = #{rcno} AND rno = #{rno};
		            UPDATE fm_review_comment SET delete_ts = current_date WHERE parent_rcno = #{rcno};
		        END
    		}


            UPDATE fm_review
			SET comment_cnt=comment_cnt+#{amount}
			WHERE rno=#{rno}

mapper.delete:

DELETE FROM fm_review_liked
WHERE rno=#{rno}
AND user_id=#{userId}

mapper.update:

            UPDATE fm_review
			SET like_cnt=like_cnt+#{amount}
			WHERE rno=#{rno}

mapper.select:

SELECT count(*)
FROM fm_review_liked
WHERE rno=#{rno}
AND user_id=#{userId}
